import React, { useEffect, useState } from 'react';
import { useParams, Link, Outlet, useNavigate } from 'react-router-dom';
import { fetchMovieDetails } from '../api/movieAPI';
import styles from '../SharedLayout/SharedLayout.module.css';

const MovieDetails = () => {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const navigate = useNavigate(); // Hook do nawigacji

  useEffect(() => {
    fetchMovieDetails(movieId).then(setMovie);
  }, [movieId]);

  const handleGoBack = () => {
    navigate(-1); // Funkcja do powrotu do poprzedniej strony
  };

  if (!movie) return <div>Loading...</div>;

  return (
    <div className={styles.container}>
      <button onClick={handleGoBack}>Go back</button> {/* Przycisk powrotu */}
      <h1>{movie.title}</h1>
      <img
        src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
        alt={movie.title}
      />
      <p>
        <strong>User Score:</strong> {movie.vote_average * 10}%
      </p>
      <p>
        <strong>Overview:</strong> {movie.overview}
      </p>
      <p>
        <strong>Genres:</strong>{' '}
        {movie.genres.map(genre => genre.name).join(', ')}
      </p>
      <h2>Additional Informations</h2>
      <div>
        <Link to={`/movies/${movieId}/reviews`}>Reviews</Link>
        <Link to={`/movies/${movieId}/cast`}>Cast</Link>
      </div>
      <Outlet />
    </div>
  );
};

export default MovieDetails;
